import React, { useEffect, useState } from "react";
import axios from "axios";
import fetchQuotes from "./api/api"; // Update the path to match the actual location
import { prettyDOM } from "@testing-library/react";

const App = () => {
  const [pokemonData, setPokemonData] = useState([]);
  // const [pokemonData, setpokemonData] = useState([]);
  const [loading, setLoading] = useState(true);


  // useEffect to call the API.
  useEffect(() => {
    const fetchData = async () => {
      try {
        const pokemonData = await fetchQuotes();
        setPokemonData(pokemonData.results);
        await getPokemon(pokemonData.results);
        setLoading(false);
        console.log(pokemonData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // A function used to extract just the individual pokemon.
  const getPokemon = async (res) => {
    try {
      const pokemonPromises = res.map(async (item) => {
        const result = await axios.get(item.url);
        return result.data;
      });

      const pokemonData = await Promise.all(pokemonPromises);
      setPokemonData((prevData) => [...prevData, ...pokemonData]);
    } catch (error) {
      console.error("Error fetching Pokémon data:", error);
    }
  };

  console.log(pokemonData);

  return (
    <div>
      <h1>Pokemon Abilities</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {/* <div className="border">
            <h2 className="font-bold text-2xl">Moves:</h2>
            {pokemonData.moves.map((movesObj, index) => (
              <span key={index}>{movesObj.move.name}, </span>
            ))}
          </div> */}
          <div>
            <h2 className="font-bold text-2xl">Types:</h2>
            <ul>
              {/* {pokemonData.types.map((typesObj, index) => (
                <li key={index}>{typesObj.type.name}</li>
              ))} */}
            </ul>
          </div>
          <div>
            <h2 className="font-bold text-2xl">Stats:</h2>
            <ul>
              {/* {pokemonData.stats.map((statsObj, index) => (
                <li key={index}>
                  Base Stat: {statsObj.base_stat} Name: {statsObj.stat.name}
                </li>
              ))} */}
            </ul>
          </div>
          {/* <img src={pokemonData.sprites.front_default} alt="" />
          <img src={pokemonData.sprites.back_default} alt="" /> */}
          {/* {pokemonData.sprites.map((spritesObj, index) => (
            <img src={spritesObj.front_default} alt="" />
          ))} */}
          {/* <pre>{JSON.stringify(pokemonData.moves, null, 2)}</pre> */}
        </div>
      )}
    </div>
  );
};
export default App;
