import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 5;

  const allMoves = pokemonData[currentSelectedPokemon].moves.sort((a, b) => {
    const levelA = a.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
    const levelB = b.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
    return levelA - levelB;
  });

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = allMoves.slice(indexOfFirstMove, indexOfLastMove);

  const totalPages = Math.ceil(allMoves.length / movesPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="w-full border rounded">
      <table className="w-full">{/* ... Table content ... */}</table>
      <div className="flex justify-center mt-4">
        <button
          onClick={handlePrevPage}
          className={`mx-2 ${
            currentPage === 1 ? "bg-gray-300" : "bg-blue-500 text-white"
          } px-4 py-2 rounded-full`}
        >
          Previous
        </button>
        <button
          onClick={handleNextPage}
          className={`mx-2 ${
            currentPage === totalPages
              ? "bg-gray-300"
              : "bg-blue-500 text-white"
          } px-4 py-2 rounded-full`}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PokemonMovesTable;
