import React from "react";

const PokemonInfoCard = ({ pokemonData, currentSelectedPokemon }) => {
  const statAbbreviations = {
    hp: "HP",
    attack: "Atk.",
    defense: "Def.",
    "special-attack": "Sp. Atk.",
    "special-defense": "Sp. Def.",
    speed: "Spd.",
  };

  // Split the stats into rows of three columns each
  const statsRows = [];
  for (
    let i = 0;
    i < pokemonData[currentSelectedPokemon].stats.length;
    i += 3
  ) {
    statsRows.push(pokemonData[currentSelectedPokemon].stats.slice(i, i + 3));
  }

  return (
    <div className="flex border justify-center rounded">
      {statsRows.map((row, rowIndex) => (
        <div key={rowIndex} className="flex flex-wrap">
          {row.map((stat) => (
            <div key={stat.stat.name} className="w-1/3 border p-4">
              <p className="text-2xl font-bold">
                {statAbbreviations[stat.stat.name] || stat.stat.name}
              </p>
              <p className="text-lg mt-2 text-center">{stat.base_stat}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default PokemonInfoCard;
