import React, { useEffect, useState } from "react";
import PokemonInfoCard from "./PokemonInfoCard";
import PokemonMovesCard from "./PokemonMovesCard";
import PokemonEvolutionsCard from "./PokemonEvolutionsCard";
import PokemonCard from "./PokemonCard";
import axios from "axios";

const PokemonPage = ({
  currentSelectedPokemon,
  pokemonData,
  handleBackButton,
  formatText,
}) => {
  const [loading, setLoading] = useState(true);

  const pokemonName = currentSelectedPokemon.split("-")[0];
  const pokemonID = parseInt(currentSelectedPokemon.split("-")[1] - 1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon-species/${pokemonName}/`
        );

        const evolutionChainUrl = response.data.evolution_chain.url;
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [pokemonName]);

  return (
    <div className="mx-4 md:mx-20">
      <button className="mt-4 md:mt-8" onClick={handleBackButton}>
        Back
      </button>
      <div className="text-2xl md:text-5xl">
        {pokemonData[pokemonID].name.toUpperCase()}
      </div>
      <EvolutionChain pokemonId={pokemonID} />{" "}
      {/* Use the EvolutionChain component */}
      <PokemonCard />
      <div className="flex flex-col mt-4 md:flex-row md:gap-4">
        <div className="w-full md:w-2/5 my-4">
          <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
            Stats
          </div>
          <PokemonInfoCard
            pokemonData={pokemonData}
            currentSelectedPokemon={pokemonID}
          />
        </div>
        <div className="w-full md:w-3/5 my-4">
          <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
            Moves
          </div>
          <PokemonMovesCard
            pokemonData={pokemonData}
            currentSelectedPokemon={pokemonID}
            formatText={formatText}
          />
        </div>
      </div>
    </div>
  );
};

export default PokemonPage;
