import React, { useEffect, useState } from "react";
import PokemonInfoCard from "./PokemonInfoCard";
import PokemonMovesCard from "./PokemonMovesCard";
import PokemonEvolutionsCard from "./PokemonEvolutionsCard";
import PokemonCard from "./PokemonCard";
import axios from "axios";

const PokemonPage = ({
  currentSelectedPokemon,
  pokemonData,
  handleBackButton,
  formatText,
}) => {
  const [evolutionData, setEvolutionData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch the current Pokémon species data
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon-species/${
            currentSelectedPokemon + 1
          }/`
        );

        // Extract the URL of the evolution chain from the response data
        const evolutionChainUrl = response.data.evolution_chain.url;

        // Fetch the evolution chain data
        const evolData = await axios.get(evolutionChainUrl);
        console.log(evolData.results);

        // const matchingPokemon = pokemonData.find(
        //   (pokemon) =>
        //     pokemon.name === evolData.data.chain.evolves_to[0].species.name
        // );
        // if (matchingPokemon) {
        //   console.log(matchingPokemon);
        // } else {
        //   console.log("dafas");
        // }
        // Set the evolution data in your state
        setEvolutionData(evolData.data);

        // Finally, set loading to false
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [currentSelectedPokemon]); // Include currentSelectedPokemon in the dependency array

  return (
    <div className="mx-4 md:mx-20">
      <button className="mt-4 md:mt-8" onClick={handleBackButton}>
        Back
      </button>
      <div className="text-2xl md:text-5xl">
        {pokemonData[currentSelectedPokemon].name.toUpperCase()}
      </div>
      <PokemonEvolutionsCard
        pokemonData={pokemonData}
        currentSelectedPokemon={currentSelectedPokemon}
      />
      <PokemonCard />
      <div className="flex flex-col mt-4 md:flex-row md:gap-4">
        <div className="w-full md:w-2/5 my-4">
          <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
            Stats
          </div>
          <PokemonInfoCard
            pokemonData={pokemonData}
            currentSelectedPokemon={currentSelectedPokemon}
          />
        </div>
        <div className="w-full md:w-3/5 my-4">
          <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
            Moves
          </div>
          <PokemonMovesCard
            pokemonData={pokemonData}
            currentSelectedPokemon={currentSelectedPokemon}
            formatText={formatText}
          />
        </div>
      </div>
    </div>
  );
};

export default PokemonPage;
