import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 10;

  const allMoves = pokemonData[currentSelectedPokemon].moves;

  // Sort moves by level_learned_at in ascending order
  // allMoves.sort((a, b) => {
  //   const levelA = a.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
  //   const levelB = b.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
  //   return levelA - levelB;
  // });

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = allMoves.slice(indexOfFirstMove, indexOfLastMove);

  const totalPages = Math.ceil(allMoves.length / movesPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="w-full">
      <table className="w-full">
        <thead>
          <tr>
            <th className="px-4 py-2">Move Name</th>
            <th className="px-4 py-2">Level Learned At</th>
            <th className="px-4 py-2">Learn Method</th>
          </tr>
        </thead>
        <tbody>
          {currentMoves.map((move, index) => (
            <tr key={index}>
              <td className="border px-4 py-2">
                <p className="font-bold">Name: </p>
                <p className="">{formatText(move.move.name)}</p>
              </td>
              <td className="border px-4 py-2">
                <p className="font-bold">Lvl. Learned: </p>
                {move.version_group_details[0]?.level_learned_at || "N/A"}
              </td>
              <td className="border px-4 py-2">
                <p className="font-bold">Obtained By: </p>
                {move.version_group_details[0].move_learn_method.name ===
                "machine"
                  ? "TM"
                  : formatText(
                      move.version_group_details[0]?.move_learn_method.name ||
                        "N/A"
                    )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center mt-4">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={`mx-2 ${
              currentPage === index + 1
                ? "bg-blue-500 text-white"
                : "bg-gray-300"
            } px-3 py-1 rounded-full`}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default PokemonMovesTable;
