import React, { useEffect, useState } from "react";
import fetchQuotes from "./api/api"; // Update the path to match the actual location

const App = () => {
  const [quotes, setQuotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const quotesData = await fetchQuotes();
        setQuotes(quotesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // console.log(quotes.abilities[0].ability);

  // Check if quotes is empty before accessing its elements
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Quotes App</h1>
      {/* Render your content using the fetched data */}
      {quotes.map((quote, index) => (
        <p key={index}>{quote.name}</p>
      ))}
    </div>
  );
};
export default App;
