import React, { useEffect, useState } from "react";
import fetchEvolutionChain from "../api/api";
import axios from "axios";

const EvolutionChain = ({ pokemonId }) => {
  const [evolutionChain, setEvolutionChain] = useState({});

  useEffect(() => {
    fetchEvolutionChain(pokemonId,);
  }, [pokemonId]);

  console.log(evolutionChain);
  return (
    <div>
      <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
        Evolution Chain
      </div>
      <ul>
        {Object.entries(evolutionChain).map(([pokemon, imageUrl], index) => (
          <li className="flex" key={index}>
            <img src={imageUrl} alt={`Image of ${pokemon}`} /> {pokemon}
            <p></p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EvolutionChain;
