import React, { useEffect, useState } from "react";
import axios from "axios";

const EvolutionChain = ({ pokemonId }) => {
  const [evolutionChain, setEvolutionChain] = useState([]);
  const [pokemonImage, setPokemonImage] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvolutionChain = async () => {
      try {
        // Fetch the Pokémon species data to get the evolution chain URL
        const speciesResponse = await axios.get(
          `https://pokeapi.co/api/v2/pokemon-species/${pokemonId + 1}/`
        );

        // Extract the evolution chain URL
        const evolutionChainUrl = speciesResponse.data.evolution_chain.url;

        // Fetch the evolution chain data
        const evolutionChainResponse = await axios.get(evolutionChainUrl);

        // Extract the evolution chain details
        let chain = evolutionChainResponse.data.chain;

        // Fetch the Pokémon's image
        const pokemonResponse = await axios.get(
          `https://pokeapi.co/api/v2/pokemon/${pokemonId + 1}/`
        );

        // Set the Pokémon's image in state
        setPokemonImage(pokemonResponse.data.sprites.front_default);

        // Traverse the evolution chain and store Pokémon names
        const evolutionNames = [];
        let currentPokemon = chain.species.name;
        evolutionNames.push(currentPokemon);

        while (chain.evolves_to.length > 0) {
          currentPokemon = chain.evolves_to[0].species.name;
          evolutionNames.push(currentPokemon);
          chain = chain.evolves_to[0];
        }

        // Set the evolution chain in state
        setEvolutionChain(evolutionNames);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching evolution chain:", error);
      }
    };

    fetchEvolutionChain();
  }, [pokemonId]);

  if (loading) {
    return <div>Loading evolution chain...</div>;
  }

  return (
    <div>
      <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
        Evolution Chain
      </div>
      {pokemonImage && (
        <img
          src={pokemonImage}
          alt={`Image of ${evolutionChain[0]}`}
          className="w-32 h-32 mx-auto"
        />
      )}
      <ul>
        {evolutionChain.map((pokemon, index) => (
          <li key={index}>{pokemon}</li>
        ))}
      </ul>
    </div>
  );
};

export default EvolutionChain;
