import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const movesPerPage = 5;

  const allMoves = pokemonData[currentSelectedPokemon].moves.sort((a, b) => {
    const levelA = a.version_group_details[0]?.level_learned_at || 999;
    const levelB = b.version_group_details[0]?.level_learned_at || 999;
    return levelA - levelB;
  });

  const totalPages = Math.ceil(allMoves.length / movesPerPage);

  // Initialize the state to keep track of the visible page
  const [currentPage, setCurrentPage] = useState(1);

  // Calculate the range of moves to display for the current page
  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = allMoves.slice(indexOfFirstMove, indexOfLastMove);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="w-full border rounded-3xl p-2">
      <div className="overflow-y-auto max-h-96">
        {" "}
        {/* Add max height for scrolling */}
        <table className="w-full">
          <thead>
            <tr className="border-b">
              <th className="px-6 py-4">Move Name</th>
              <th className="px-6 py-4">Level Learned At</th>
              <th className="px-6 py-4">Learn Method</th>
            </tr>
          </thead>
          <tbody className="text-center">
            {currentMoves.map((move, index) => (
              <tr className="border-b" key={index}>
                <td className="px-6 py-4">
                  <span>{formatText(move.move.name)}</span>
                </td>
                <td className="px-6 py-4">
                  {move.version_group_details[0]?.level_learned_at || "N/A"}
                </td>
                <td className="px-6 py-4">
                  {move.version_group_details[0].move_learn_method.name ===
                  "machine"
                    ? "TM"
                    : formatText(
                        move.version_group_details[0]?.move_learn_method.name ||
                          "N/A"
                      )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="flex justify-end my-4">
        <button
          onClick={handlePrevPage}
          className={`mx-2 ${
            currentPage === 1 ? "bg-gray-300" : "bg-blue-500 text-white"
          } px-4 py-2 rounded`}
        >
          Prev
        </button>
        <button
          onClick={handleNextPage}
          className={`mx-2 ${
            currentPage === totalPages
              ? "bg-gray-300"
              : "bg-blue-500 text-white"
          } px-4 py-2 rounded`}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PokemonMovesTable;
