import React, { useEffect, useState } from "react";
import axios from "axios";

const EvolutionChain = ({ pokemonId }) => {
  const [evolutionChain, setEvolutionChain] = useState([]);

  useEffect(() => {
    const fetchEvolutionChain = async () => {
      try {
        const speciesResponse = await axios.get(
          `https://pokeapi.co/api/v2/pokemon-species/${pokemonId + 1}/`
        );
        const evolutionChainUrl = speciesResponse.data.evolution_chain.url;
        const evolutionChainResponse = await axios.get(evolutionChainUrl);
        let chain = evolutionChainResponse.data.chain;
        const evolutionNames = [chain.species.name];

        while (chain.evolves_to.length > 0) {
          chain = chain.evolves_to[0];
          evolutionNames.push(chain.species.name);
        }

        setEvolutionChain(evolutionNames);
      } catch (error) {
        console.error("Error fetching evolution chain:", error);
      }
    };

    fetchEvolutionChain();
  }, [pokemonId]);

  return (
    <div>
      <div className="text-xl md:text-2xl font-bold mb-2 md:mb-4">
        Evolution Chain
      </div>
      <ul>
        {evolutionChain.map((pokemon, index) => (
          <li key={index}>{pokemon}</li>
        ))}
      </ul>
    </div>
  );
};

export default EvolutionChain;
