const renderPagination = () => {
  const buttons = [];
  const maxButtons = 4; // Change this value to control the number of clickable pages

  if (totalPages <= maxButtons) {
    // If there are fewer or equal to maxButtons pages, display all page buttons
    for (let i = 1; i <= totalPages; i++) {
      buttons.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={`mx-2 ${
            currentPage === i ? "bg-blue-500 text-white" : "bg-gray-300"
          } px-4 py-2 rounded`}
        >
          {i}
        </button>
      );
    }
  } else {
    // If there are more than maxButtons pages, display first, last, and ellipsis buttons
    const firstPageButton = (
      <button
        key={1}
        onClick={() => handlePageChange(1)}
        className={`mx-2 ${
          currentPage === 1 ? "bg-blue-500 text-white" : "bg-gray-300"
        } px-4 py-2 rounded`}
      >
        1
      </button>
    );
    const lastPageButton = (
      <button
        key={totalPages}
        onClick={() => handlePageChange(totalPages)}
        className={`mx-2 ${
          currentPage === totalPages ? "bg-blue-500 text-white" : "bg-gray-300"
        } px-4 py-2 rounded`}
      >
        {totalPages}
      </button>
    );

    buttons.push(firstPageButton);

    // Calculate the range of buttons to display around the current page
    const start = Math.max(2, currentPage - Math.floor(maxButtons / 2));
    const end = Math.min(totalPages - 1, start + maxButtons - 3);

    if (start > 2) {
      buttons.push(
        <button
          key={"ellipsis-start"}
          className="mx-2 bg-gray-300 px-4 py-2 rounded"
          disabled={true}
        >
          ...
        </button>
      );
    }

    for (let i = start; i <= end; i++) {
      buttons.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={`mx-2 ${
            currentPage === i ? "bg-blue-500 text-white" : "bg-gray-300"
          } px-4 py-2 rounded`}
        >
          {i}
        </button>
      );
    }

    if (end < totalPages - 1) {
      buttons.push(
        <button
          key={"ellipsis-end"}
          className="mx-2 bg-gray-300 px-4 py-2 rounded"
          disabled={true}
        >
          ...
        </button>
      );
    }

    buttons.push(lastPageButton);
  }

  return buttons;
};
