import React from "react";

const FullPokemonList = ({ pokemonData }) => {
  console.log(pokemonData);
  return (
    <>
      {pokemonData && pokemonData.length > 0 && (
        <div className="flex flex-wrap justify-center align-center gap-2 p-20">
          {pokemonData.map((item, index) => (
            <div key={index}>
              <div className="p-4 pokemon-card rounded shadow-md">
                <div className="card-details flex justify-between">
                  <p className="text-lg font-bold">{item.name.toUpperCase()}</p>
                  <p className="text-md">{`#${item.id
                    .toString()
                    .padStart(4, "0")}`}</p>
                </div>
                <div
                  className="image-container flex justify-center border max-w"
                  style={{ maxWidth: "100%", overflow: "hidden" }}
                >
                  <img
                    className="max-h-48" // Adjust the max height as needed
                    src={item.sprites.front_default}
                    alt=""
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default FullPokemonList;
