import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 5;

  const allMoves = pokemonData[currentSelectedPokemon].moves.sort((a, b) => {
    const levelA = a.version_group_details[0]?.level_learned_at || 999;
    const levelB = b.version_group_details[0]?.level_learned_at || 999;
    return levelA - levelB;
  });

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = allMoves.slice(indexOfFirstMove, indexOfLastMove);

  const totalPages = Math.ceil(allMoves.length / movesPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const renderPagination = () => {
    const pageNumbers = Array.from(
      { length: totalPages },
      (_, index) => index + 1
    );

    const visiblePages = [];
    const minPage = Math.max(currentPage - 2, 1);
    const maxPage = Math.min(currentPage + 2, totalPages);

    if (minPage > 1) {
      visiblePages.push(1);
      if (minPage > 2) {
        visiblePages.push("...");
      }
    }

    for (let i = minPage; i <= maxPage; i++) {
      visiblePages.push(i);
    }

    if (maxPage < totalPages) {
      if (maxPage < totalPages - 1) {
        visiblePages.push("...");
      }
      visiblePages.push(totalPages);
    }

    return (
      <div className="flex justify-center mt-4 border">
        {visiblePages.map((pageNumber, index) => (
          <button
            key={index}
            onClick={() =>
              typeof pageNumber === "number" && handlePageChange(pageNumber)
            }
            className={`mx-2 ${
              pageNumber === currentPage
                ? "bg-blue-500 text-white"
                : "bg-gray-300"
            } px-4 py-2 rounded-full`}
          >
            {pageNumber}
          </button>
        ))}
      </div>
    );
  };

  return (
    <div className="w-full border rounded">
      <table className="w-full">{/* ... Table content ... */}</table>
      {renderPagination()}
    </div>
  );
};

export default PokemonMovesTable;
