import React, { useEffect, useState } from "react";
import axios from "axios";
import fetchQuotes from "./api/api";

const App = () => {
  const [pokemonData, setPokemonData] = useState([]);
  const [loading, setLoading] = useState(true);

  const [apiLimit, setAPILimit] = useState(20);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const pokemonData = await fetchQuotes(apiCount);
        getPokemon(pokemonData.results);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const getPokemon = async (res) => {
    try {
      const pokemonPromises = res.map(async (item) => {
        const result = await axios.get(item.url);
        return result.data;
      });

      const pokemonData = await Promise.all(pokemonPromises);
      setPokemonData(pokemonData);
    } catch (error) {
      console.error("Error fetching Pokémon data:", error);
    }
  };

  const getMorePokemon = () => {
    setAPILimit((val) => val + 20);
  };

  return (
    <div>
      <h1>Pokemon Abilities</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <div>
            <h2 className="font-bold text-2xl">Types:</h2>
            <ul>
              {pokemonData.map((item, index) => (
                <div>
                  <div className="border" key={index}>
                    {item.name}
                    {item.id}
                    <img src={item.sprites.front_default} alt="" />
                    <img src={item.sprites.back_default} alt="" />
                  </div>
                </div>
              ))}
            </ul>
          </div>
          <button onClick={getMorePokemon}>More</button>
          {/* <img src={pokemonData.sprites.front_default} alt="" />
          <img src={pokemonData.sprites.back_default} alt="" /> */}
          {/* {pokemonData.sprites.map((spritesObj, index) => (
            <img src={spritesObj.front_default} alt="" />
          ))} */}
          {/* <pre>{JSON.stringify(pokemonData.moves, null, 2)}</pre> */}
        </div>
      )}
    </div>
  );
};
export default App;
