import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 10;

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = pokemonData[currentSelectedPokemon].moves.slice(
    indexOfFirstMove,
    indexOfLastMove
  );

  const totalPages = Math.ceil(
    pokemonData[currentSelectedPokemon].moves.length / movesPerPage
  );

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="w-full">
      <table className="w-full table-auto">
        <thead>
          <tr>
            <th className="px-4 py-2">Move Name</th>
            <th className="px-4 py-2">Level Learned At</th>
            <th className="px-4 py-2">Learn Method</th>
          </tr>
        </thead>
        <tbody>
          {currentMoves.map((move, index) => (
            <tr key={index}>
              <td className="border px-4 py-2 font-bold">
                {formatText(move.move.name)}
              </td>
              <td className="border px-4 py-2">
                {move.version_group_details[0].level_learned_at}
              </td>
              <td className="border px-4 py-2">
                Learn Method:
                {move.version_group_details[0].move_learn_method.name ===
                "machine"
                  ? "TM"
                  : formatText(
                      move.version_group_details[0].move_learn_method.name
                    )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center mt-4">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={`mx-2 ${
              currentPage === index + 1
                ? "bg-blue-500 text-white"
                : "bg-gray-300"
            } px-3 py-1 rounded-full`}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default PokemonMovesTable;
