import React, { useEffect, useState } from "react";
import fetchQuotes from "./api/api"; // Update the path to match the actual location

const App = () => {
  const [quotes, setQuotes] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const quotesData = await fetchQuotes();
        setQuotes(quotesData);
        // console.log(quotesData);
        // console.log(typeof quotesData);
        console.log(quotesData.abilities[0].ability);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // console.log(quotes.abilities[0].ability);

  // Check if quotes is empty before accessing its elements
  if (quotes.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Quotes App</h1>
      <p>{quotes[0].name}</p> {/* Display the name property */}
    </div>
  );
};
export default App;
