import React, { useState } from "react";

const PokemonMovesCard = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 10;

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = pokemonData[currentSelectedPokemon].moves.slice(
    indexOfFirstMove,
    indexOfLastMove
  );

  const totalPages = Math.ceil(
    pokemonData[currentSelectedPokemon].moves.length / movesPerPage
  );

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  console.log()

  return (
    <>
      {currentMoves.map(({ move }) => (
        <div>
          <p key={move.name}>{formatText(move.name)}</p>
          <p key={move.name}>{move.version_group_details}</p>
        </div>
      ))}

      <div className="flex justify-center">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={`mx-2 ${
              currentPage === index + 1
                ? "bg-blue-500 text-white"
                : "bg-gray-300"
            } px-3 py-1 rounded-full`}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </>
  );
};

export default PokemonMovesCard;
