import React, { useState } from "react";

const PokemonMovesTable = ({
  pokemonData,
  currentSelectedPokemon,
  formatText,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const movesPerPage = 5;

  const allMoves = pokemonData[currentSelectedPokemon].moves.sort((a, b) => {
    const levelA = a.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
    const levelB = b.version_group_details[0]?.level_learned_at || 999; // Use 999 as a default value
    return levelA - levelB;
  });

  const indexOfLastMove = currentPage * movesPerPage;
  const indexOfFirstMove = indexOfLastMove - movesPerPage;
  const currentMoves = allMoves.slice(indexOfFirstMove, indexOfLastMove);

  const totalPages = Math.ceil(allMoves.length / movesPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="w-full border rounded p-4">
      <table className="w-full">
        {/* <thead>
          <tr>
            <th className="px-4 py-2">Move Name</th>
            <th className="px-4 py-2">Level Learned At</th>
            <th className="px-4 py-2">Learn Method</th>
          </tr>
        </thead> */}
        <tbody className="">
          {currentMoves.map((move, index) => (
            <tr className="border-b" key={index}>
              <td className=" px-4 py-2">
                <span className="font-bold">Name: </span>
                <span className="">{formatText(move.move.name)}</span>
              </td>
              <td className=" px-4 py-2">
                <span className="font-bold">Lvl. Learned: </span>
                {move.version_group_details[0]?.level_learned_at || "N/A"}
              </td>
              <td className=" px-4 py-2">
                <p className="font-bold">Obtained By: </p>
                {move.version_group_details[0].move_learn_method.name ===
                "machine"
                  ? "TM"
                  : formatText(
                      move.version_group_details[0]?.move_learn_method.name ||
                        "N/A"
                    )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center my-4">
        <button
          onClick={handlePrevPage}
          className={`mx-2 ${
            currentPage === 1 ? "bg-gray-300" : "bg-blue-500 text-white"
          } px-4 py-2 rounded`}
        >
          Prev
        </button>
        <button
          onClick={handleNextPage}
          className={`mx-2 ${
            currentPage === totalPages
              ? "bg-gray-300"
              : "bg-blue-500 text-white"
          } px-4 py-2 rounded`}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PokemonMovesTable;
