import React from "react";

const FullPokemonList = ({ pokemonData }) => {
  console.log(pokemonData);
  return (
    <>
      {pokemonData && pokemonData.length > 0 && (
        <div className="border flex flex-wrap justify-center align-center gap-2 p-20">
          {pokemonData.map((item, index) => (
            <div>
              <div
                className="border p-4 pokemon-card rounded shadow-2xl"
                key={index}
              >
                <div className="card-details flex justify-between">
                  <p className="text-lg font-bold">
                    {item.name.charAt(0).toUpperCase() + item.name.slice(1)}
                  </p>
                  <p className="text-md">{`#${item.id
                    .toString()
                    .padStart(4, "0")}`}</p>
                </div>
                <div className="flex justify-center">
                  <img
                    className="border"
                    src={item.sprites.front_default}
                    alt=""
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default FullPokemonList;

